def solve():
n = int(input())
C = [0] * n
J = [0] * n
# p0/q0 < y/x < p1/q1
p0, q0 = 0, 1
p1, q1 = 1, 0
fail = False
for i in range(n):
    C[i], J[i] = map(int, input().split())
    if i > 0:
        A = C[i] - C[i-1]
        B = J[i] - J[i-1]
        if A <= 0 and B <= 0:
            fail = True
        elif B > 0 and A < 0: # y/x > (-A)/B if B > 0
            if (-A)*q0 > p0*B:
                p0, q0 = -A, B
        elif B < 0 and A > 0: # y/x < A/(-B) if B < 0
            if A*q1 < p1*(-B):
                p1, q1 = A, -B
if p0*q1 >= p1*q0 or fail:
    return 'IMPOSSIBLE'

p, q = middle(p0, q0, p1, q1)
return str(q) + ' ' + str(p)

